-- ----------------------------
--  Table structure for `wp_posts`
-- ----------------------------
DROP TABLE IF EXISTS `wp_posts`;
CREATE TABLE `wp_posts` (
  `ID` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `post_author` bigint(20) unsigned NOT NULL DEFAULT '0',
  `post_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_date_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content` longtext NOT NULL,
  `post_title` text NOT NULL,
  `post_excerpt` text NOT NULL,
  `post_status` varchar(20) NOT NULL DEFAULT 'publish',
  `comment_status` varchar(20) NOT NULL DEFAULT 'open',
  `ping_status` varchar(20) NOT NULL DEFAULT 'open',
  `post_password` varchar(20) NOT NULL DEFAULT '',
  `post_name` varchar(200) NOT NULL DEFAULT '',
  `to_ping` text NOT NULL,
  `pinged` text NOT NULL,
  `post_modified` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_modified_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content_filtered` longtext NOT NULL,
  `post_parent` bigint(20) unsigned NOT NULL DEFAULT '0',
  `guid` varchar(255) NOT NULL DEFAULT '',
  `menu_order` int(11) NOT NULL DEFAULT '0',
  `post_type` varchar(20) NOT NULL DEFAULT 'post',
  `post_mime_type` varchar(100) NOT NULL DEFAULT '',
  `comment_count` bigint(20) NOT NULL DEFAULT '0',
  PRIMARY KEY (`ID`),
  KEY `post_name` (`post_name`),
  KEY `type_status_date` (`post_type`,`post_status`,`post_date`,`ID`),
  KEY `post_parent` (`post_parent`),
  KEY `post_author` (`post_author`)
) ENGINE=InnoDB AUTO_INCREMENT=23 DEFAULT CHARSET=utf8;

-- ----------------------------
--  Records of `wp_posts`
-- ----------------------------
BEGIN;
INSERT INTO `wp_posts` VALUES ('1', '1', '2014-08-11 03:38:24', '2014-08-11 03:38:24', '欢迎使用WordPress。这是系统自动生成的演示文章。编辑或者删除它，然后开始您的博客！', '世界，你好！', '', 'publish', 'open', 'open', '', 'hello-world', '', '', '2014-08-11 03:38:24', '2014-08-11 03:38:24', '', '0', 'http://liuxingdemacbook-pro.local/wordpress/?p=1', '0', 'post', '', '1'), ('2', '1', '2014-08-11 03:38:24', '2014-08-11 03:38:24', '这是示范页面。页面和博客文章不同，它的位置是固定的，通常会在站点导航栏显示。很多用户都创建一个“关于”页面，向访客介绍自己。例如，个人博客通常有类似这样的介绍：\n\n<blockquote>欢迎！我白天是个邮递员，晚上就是个有抱负的演员。这是我的博客。我住在天朝的帝都，有条叫做杰克的狗。</blockquote>\n\n……公司博客可以这样写：\n\n<blockquote>XYZ Doohickey公司成立于1971年，自从建立以来，我们一直向社会贡献着优秀doohicky。我们的公司总部位于天朝魔都，有着超过两千名员工，对魔都政府税收有着巨大贡献。</blockquote>\n\n您可以访问<a href=\"http://liuxingdemacbook-pro.local/wordpress/wp-admin/\">仪表盘</a>，删除本页面，然后添加您自己的内容。祝您使用愉快！', '示例页面', '', 'publish', 'open', 'open', '', 'sample-page', '', '', '2014-08-11 03:38:24', '2014-08-11 03:38:24', '', '0', 'http://liuxingdemacbook-pro.local/wordpress/?page_id=2', '0', 'page', '', '0'), ('3', '1', '2014-08-11 11:38:55', '0000-00-00 00:00:00', '', '自动草稿', '', 'auto-draft', 'open', 'open', '', '', '', '', '2014-08-11 11:38:55', '0000-00-00 00:00:00', '', '0', 'http://liuxingdemacbook-pro.local/wordpress/?p=3', '0', 'post', '', '0'), ('4', '1', '2014-08-11 13:35:45', '0000-00-00 00:00:00', '', '首页', '', 'draft', 'open', 'open', '', '', '', '', '2014-08-11 13:35:45', '0000-00-00 00:00:00', '', '0', 'http://liuxingdemacbook-pro.local/wordpress/?p=4', '1', 'nav_menu_item', '', '0'), ('5', '1', '2014-08-11 13:35:45', '0000-00-00 00:00:00', ' ', '', '', 'draft', 'open', 'open', '', '', '', '', '2014-08-11 13:35:45', '0000-00-00 00:00:00', '', '0', 'http://liuxingdemacbook-pro.local/wordpress/?p=5', '1', 'nav_menu_item', '', '0'), ('6', '1', '2014-08-11 14:08:33', '2014-08-11 06:08:33', '#<span style=\"color: #000000;\"> <span style=\"font-weight: bold;\">机器人的世界也看脸？</span><span style=\"font-weight: bold;\">机器人的世界也看脸？</span></span>\n\n&nbsp;\n\n<p style=\"color: #333333;\">机器人最早诞生于上世纪60年代，它们大多躺在工厂车间内，为节约人力而存在。那个时代的机器人更偏重“机器”二字，毫无外形美感而言。</p>\n\n<p style=\"color: #333333;\">此后，随着机器人的身影逐渐走进人类生活，它们的面貌也相应地有了改观，外形渐渐贴近了人的样子，不过从设计风格上，仍然没有摆脱“机甲”带来的冰冷感。</p>\n\n<p style=\"color: #333333;\">然而近来，越来越多的机器人有了浑圆的身材，多变的眼神，大大的笑容，也有了与人进行情感互动的功能。单单看你一眼，就萌的你内心融化，血脉膨胀。</p>\n\n<p style=\"color: #333333;\">机器人世界似乎进入了一个卖萌的时代。</p>\n\n<div class=\"cp-video\">\n<div class=\"title\">\n<h3>Galaxy S4 with Flyme 3.x Hands on</h3>\n<time datetime=\"2014-07-29\">13 天前</time>\n\n</div>\n<div id=\"youkuplayer\"><object id=\"youku-player\" width=\"100%\" height=\"100%\" type=\"application/x-shockwave-flash\" data=\"http://player.youku.com/player.php/sid/XNzM1ODkyNzY4/partnerid/2cf898d1c36ea115/v.swf\"><param name=\"allowFullScreen\" value=\"true\"><param name=\"allowScriptAccess\" value=\"always\"><param name=\"movie\" value=\"http://player.youku.com/player.php/sid/XNzM1ODkyNzY4/partnerid/2cf898d1c36ea115/v.swf\"><param name=\"flashvars\" value=\"imglogo=&amp;paid=0&amp;partnerId=2cf898d1c36ea115&amp;styleid=1\"></object></div>\n<div class=\"video_info\">\n<div class=\"video_author_info\"></div>\n<div class=\"video_desc\"><a href=\"http://www.zhihu.com/people/Deng088191\" target=\"_blank\">\n</a>\n<h3>文森王Vincewong</h3>\n</div>\n<div class=\"description post\">\n\nProduced by Vincewong\n\n</div>\n</div>\n</div>\n\n<h3 id=\"baxter\" style=\"color: #333333;\">Baxter</h3>\n\n<blockquote style=\"color: #333333;\">Baxter 是由 Rethink 公司倾力打造的一款双臂工业机器人，也是第一款能够在生产线上与人们协同工作的机器人。\n\n周身红黑色经典搭配设计，四肢健美发达。它还长着一张会卖萌的脸，浓眉毛大眼睛，可以展示出丰富多样的表情。\n\nBaxter 还有着和人类似的行为特征。生气时会瞪眼，尴尬时会耸肩，结实新朋友时会眨眼睛表示亲近，行动前会看看自己的双臂，呆萌地考虑一下要用哪一只手。</blockquote>\n\n<p style=\"color: #333333;\"><img class=\"lazy\" src=\"http://www.bigertech.com/content/images/2014/Aug/baxter-2.jpg\" alt=\"bigertech\" data-original=\"/content/images/2014/Aug/baxter-2.jpg\" /></p>\n\n<h3 id=\"hitchbox\" style=\"color: #333333;\">HitchBox</h3>\n\n<blockquote style=\"color: #333333;\">今年7月，一只名为 HitchBox 的机器人着实火了一把。它用“搭便车”的方式穿越了整个加拿大，从 Halifax 到 Toronto，仅用了不到一周的时间。\n\nHitchBox 全身有着口香糖一样软绵绵的质感，头上的 LED 屏上永远都挂着灿烂的笑容，脚上穿着一双亮眼的黄色橡胶靴子。\n\n如果你开着车在路上发现了它，它便会央求你载它一程。然后坐上你的副驾驶位，一路上跟你有说有笑。每完成一段路程，HitchBox 还会把经历发到 Twitter 上。\n\n据说不久之后，HitchBox 还将在英属哥伦比亚开启它的另一段旅程。</blockquote>', '机器人的世界也看脸', '', 'publish', 'open', 'open', '', '6', '', '', '2014-08-11 14:26:33', '2014-08-11 06:26:33', '#<span style=\"color: #000000;\"> <span style=\"font-weight: bold;\">机器人的世界也看脸？</span><span style=\"font-weight: bold;\">机器人的世界也看脸？</span></span>\r\n\r\n&nbsp;\r\n<p style=\"color: #333333;\">机器人最早诞生于上世纪60年代，它们大多躺在工厂车间内，为节约人力而存在。那个时代的机器人更偏重“机器”二字，毫无外形美感而言。</p>\r\n<p style=\"color: #333333;\">此后，随着机器人的身影逐渐走进人类生活，它们的面貌也相应地有了改观，外形渐渐贴近了人的样子，不过从设计风格上，仍然没有摆脱“机甲”带来的冰冷感。</p>\r\n<p style=\"color: #333333;\">然而近来，越来越多的机器人有了浑圆的身材，多变的眼神，大大的笑容，也有了与人进行情感互动的功能。单单看你一眼，就萌的你内心融化，血脉膨胀。</p>\r\n<p style=\"color: #333333;\">机器人世界似乎进入了一个卖萌的时代。</p>\r\n\r\n<div class=\"cp-video\">\r\n<div class=\"title\">\r\n<h3>Galaxy S4 with Flyme 3.x Hands on</h3>\r\n<time datetime=\"2014-07-29\">13 天前</time>\r\n\r\n</div>\r\n<div id=\"youkuplayer\"><object id=\"youku-player\" width=\"100%\" height=\"100%\" type=\"application/x-shockwave-flash\" data=\"http://player.youku.com/player.php/sid/XNzM1ODkyNzY4/partnerid/2cf898d1c36ea115/v.swf\"><param name=\"allowFullScreen\" value=\"true\"><param name=\"allowScriptAccess\" value=\"always\"><param name=\"movie\" value=\"http://player.youku.com/player.php/sid/XNzM1ODkyNzY4/partnerid/2cf898d1c36ea115/v.swf\"><param name=\"flashvars\" value=\"imglogo=&amp;paid=0&amp;partnerId=2cf898d1c36ea115&amp;styleid=1\"></object></div>\r\n<div class=\"video_info\">\r\n<div class=\"video_author_info\"></div>\r\n<div class=\"video_desc\"><a href=\"http://www.zhihu.com/people/Deng088191\" target=\"_blank\">\r\n</a>\r\n<h3>文森王Vincewong</h3>\r\n</div>\r\n<div class=\"description post\">\r\n\r\nProduced by Vincewong\r\n\r\n</div>\r\n</div>\r\n</div>\r\n<h3 id=\"baxter\" style=\"color: #333333;\">Baxter</h3>\r\n<blockquote style=\"color: #333333;\">Baxter 是由 Rethink 公司倾力打造的一款双臂工业机器人，也是第一款能够在生产线上与人们协同工作的机器人。\r\n\r\n周身红黑色经典搭配设计，四肢健美发达。它还长着一张会卖萌的脸，浓眉毛大眼睛，可以展示出丰富多样的表情。\r\n\r\nBaxter 还有着和人类似的行为特征。生气时会瞪眼，尴尬时会耸肩，结实新朋友时会眨眼睛表示亲近，行动前会看看自己的双臂，呆萌地考虑一下要用哪一只手。</blockquote>\r\n<p style=\"color: #333333;\"><img class=\"lazy\" src=\"http://www.bigertech.com/content/images/2014/Aug/baxter-2.jpg\" alt=\"bigertech\" data-original=\"/content/images/2014/Aug/baxter-2.jpg\" /></p>\r\n\r\n<h3 id=\"hitchbox\" style=\"color: #333333;\">HitchBox</h3>\r\n<blockquote style=\"color: #333333;\">今年7月，一只名为 HitchBox 的机器人着实火了一把。它用“搭便车”的方式穿越了整个加拿大，从 Halifax 到 Toronto，仅用了不到一周的时间。\r\n\r\nHitchBox 全身有着口香糖一样软绵绵的质感，头上的 LED 屏上永远都挂着灿烂的笑容，脚上穿着一双亮眼的黄色橡胶靴子。\r\n\r\n如果你开着车在路上发现了它，它便会央求你载它一程。然后坐上你的副驾驶位，一路上跟你有说有笑。每完成一段路程，HitchBox 还会把经历发到 Twitter 上。\r\n\r\n据说不久之后，HitchBox 还将在英属哥伦比亚开启它的另一段旅程。</blockquote>', '0', 'http://liuxingdemacbook-pro.local/wordpress/?p=6', '0', 'post', '', '1'), ('7', '1', '2014-08-11 13:50:36', '0000-00-00 00:00:00', '', '自动草稿', '', 'auto-draft', 'open', 'open', '', '', '', '', '2014-08-11 13:50:36', '0000-00-00 00:00:00', '', '0', 'http://liuxingdemacbook-pro.local/wordpress/?page_id=7', '0', 'page', '', '0'), ('8', '1', '2014-08-11 14:08:33', '2014-08-11 06:08:33', '#<span style=\"color: #000000;\"> <span style=\"font-weight: bold;\">机器人的世界也看脸？</span><span style=\"font-weight: bold;\">机器人的世界也看脸？</span></span>\n\n&nbsp;\n\n<p style=\"color: #333333;\">\n机器人最早诞生于上世纪60年代，它们大多躺在工厂车间内，为节约人力而存在。那个时代的机器人更偏重“机器”二字，毫无外形美感而言。</p>\n\n<p style=\"color: #333333;\">此后，随着机器人的身影逐渐走进人类生活，它们的面貌也相应地有了改观，外形渐渐贴近了人的样子，不过从设计风格上，仍然没有摆脱“机甲”带来的冰冷感。</p>\n\n<p style=\"color: #333333;\">然而近来，越来越多的机器人有了浑圆的身材，多变的眼神，大大的笑容，也有了与人进行情感互动的功能。单单看你一眼，就萌的你内心融化，血脉膨胀。</p>\n\n<p style=\"color: #333333;\">机器人世界似乎进入了一个卖萌的时代。</p>\n\n<h3 id=\"baxter\" style=\"color: #333333;\">Baxter</h3>\n\n<blockquote style=\"color: #333333;\">Baxter 是由 Rethink 公司倾力打造的一款双臂工业机器人，也是第一款能够在生产线上与人们协同工作的机器人。\n\n周身红黑色经典搭配设计，四肢健美发达。它还长着一张会卖萌的脸，浓眉毛大眼睛，可以展示出丰富多样的表情。\n\nBaxter 还有着和人类似的行为特征。生气时会瞪眼，尴尬时会耸肩，结实新朋友时会眨眼睛表示亲近，行动前会看看自己的双臂，呆萌地考虑一下要用哪一只手。</blockquote>\n\n<p style=\"color: #333333;\"><img class=\"lazy\" src=\"http://www.bigertech.com/content/images/2014/Aug/baxter-2.jpg\" alt=\"bigertech\" data-original=\"/content/images/2014/Aug/baxter-2.jpg\" /></p>\n\n<h3 id=\"hitchbox\" style=\"color: #333333;\">HitchBox</h3>\n\n<blockquote style=\"color: #333333;\">今年7月，一只名为 HitchBox 的机器人着实火了一把。它用“搭便车”的方式穿越了整个加拿大，从 Halifax 到 Toronto，仅用了不到一周的时间。\n\nHitchBox 全身有着口香糖一样软绵绵的质感，头上的 LED 屏上永远都挂着灿烂的笑容，脚上穿着一双亮眼的黄色橡胶靴子。\n\n如果你开着车在路上发现了它，它便会央求你载它一程。然后坐上你的副驾驶位，一路上跟你有说有笑。每完成一段路程，HitchBox 还会把经历发到 Twitter 上。\n\n据说不久之后，HitchBox 还将在英属哥伦比亚开启它的另一段旅程。</blockquote>', '', '', 'inherit', 'open', 'open', '', '6-revision-v1', '', '', '2014-08-11 14:08:33', '2014-08-11 06:08:33', '#<span style=\"color: #000000;\"> <span style=\"font-weight: bold;\">机器人的世界也看脸？</span><span style=\"font-weight: bold;\">机器人的世界也看脸？</span></span>\r\n\r\n&nbsp;\r\n<p style=\"color: #333333;\">\r\n机器人最早诞生于上世纪60年代，它们大多躺在工厂车间内，为节约人力而存在。那个时代的机器人更偏重“机器”二字，毫无外形美感而言。</p>\r\n<p style=\"color: #333333;\">此后，随着机器人的身影逐渐走进人类生活，它们的面貌也相应地有了改观，外形渐渐贴近了人的样子，不过从设计风格上，仍然没有摆脱“机甲”带来的冰冷感。</p>\r\n<p style=\"color: #333333;\">然而近来，越来越多的机器人有了浑圆的身材，多变的眼神，大大的笑容，也有了与人进行情感互动的功能。单单看你一眼，就萌的你内心融化，血脉膨胀。</p>\r\n<p style=\"color: #333333;\">机器人世界似乎进入了一个卖萌的时代。</p>\r\n\r\n<h3 id=\"baxter\" style=\"color: #333333;\">Baxter</h3>\r\n<blockquote style=\"color: #333333;\">Baxter 是由 Rethink 公司倾力打造的一款双臂工业机器人，也是第一款能够在生产线上与人们协同工作的机器人。\r\n\r\n周身红黑色经典搭配设计，四肢健美发达。它还长着一张会卖萌的脸，浓眉毛大眼睛，可以展示出丰富多样的表情。\r\n\r\nBaxter 还有着和人类似的行为特征。生气时会瞪眼，尴尬时会耸肩，结实新朋友时会眨眼睛表示亲近，行动前会看看自己的双臂，呆萌地考虑一下要用哪一只手。</blockquote>\r\n<p style=\"color: #333333;\"><img class=\"lazy\" src=\"http://www.bigertech.com/content/images/2014/Aug/baxter-2.jpg\" alt=\"bigertech\" data-original=\"/content/images/2014/Aug/baxter-2.jpg\" /></p>\r\n\r\n<h3 id=\"hitchbox\" style=\"color: #333333;\">HitchBox</h3>\r\n<blockquote style=\"color: #333333;\">今年7月，一只名为 HitchBox 的机器人着实火了一把。它用“搭便车”的方式穿越了整个加拿大，从 Halifax 到 Toronto，仅用了不到一周的时间。\r\n\r\nH